Rule.Source:
		import java.text.SimpleDateFormat;
		import org.apache.log4j.Logger;
		import sailpoint.object.Custom;
		import sailpoint.tools.Util;
		import sailpoint.object.Application;

        Logger log = Logger.getLogger("Rule.CMH-Infor-Full-Pre-Iteration-Rule");
		log.debug("CMH-Infor-Full-Pre-Iteration-Rule: Entering :Update Custom object with the timestamp for full aggregation");
		//updates the timestamp of the task in custom object
		Custom custom = context.getObjectByName(Custom.class,"CMH-Infor-Full-Aggregation-Custom-Object");
		String format = getCustomObject("preIterationTimestamp","CMH-DateFormat");
		String incrementalAggrTime = getCustomObject("lastRunTimeStamp","CMH-Infor-Incremental-Aggregation-Custom-Object");
		try {
			 if(Util.isNotNullOrEmpty(format)) {
				SimpleDateFormat sdf = new SimpleDateFormat(format);
				String newLastRunTimeStamp = sdf.format(new Date());
				if(custom!=null){
				    if(Util.isNotNullOrEmpty(incrementalAggrTime)) {
				       custom.put("oldRunTimeStamp",incrementalAggrTime);
				    }
				    custom.put("lastRunTimeStamp",newLastRunTimeStamp);
					context.saveObject(custom);
					String appName = (String) custom.get("applicationName");
					log.debug("CMH-Infor-Full-Pre-Iteration-Rule: Update application object with query for full aggregation");
					if(Util.isNotNullOrEmpty(appName)) {
					   Application app = context.getObjectByName(Application.class,appName);
					   if(app != null) {
					      String sqlQuery = (String) custom.get("sqlQuery");
					      if(Util.isNotNullOrEmpty(sqlQuery)){
					         app.setAttribute("SQL",sqlQuery);
					      }
					   }
					   context.saveObject(app);
					}
					context.commitTransaction();
			     }
			  }
			 log.debug("CMH-Infor-Full-Pre-Iteration-Rule: Exiting :Updated Custom object and application object successfully");
		   }catch (GeneralException e) {
			     log.error("ERROR : CMH-Infor-Full-Pre-Iteration-Rule:"+e.getMessage());
		   }
