Rule.Source:
    import org.apache.log4j.Logger;  
    import sailpoint.tools.Util;

    Logger log = Logger.getLogger("cmh.authsource.rules");
    log.debug("Manager-Certification-Trigger-Rule: Entering");
    boolean flag = false;
    //return true if there is change in manager
    if(!Util.nullSafeCaseInsensitiveEq(previousIdentity.getManager().getName(),newIdentity.getManager().getName())){
  		log.debug("Manager-Certification-Trigger-Rule for identity:"+previousIdentity.getName());
  		flag= true;
	}
	log.debug("Manager-Certification-Trigger-Rule: Exiting");
	return flag;
