Rule.Source:
import sailpoint.object.Identity;
import org.apache.log4j.Logger;
import sailpoint.tools.Util;

Logger logger = Logger.getLogger("Rule.CMH-O365-Role-Assignment-Rule");

logger.debug("Entering CMH-O365-Role-Assignment-Rule:Assign O365 Role for the user based on the userType and EmailRequired");

boolean assignRole = false;
String identityName = null;

if (identity != null && identity.isCorrelated()) {
    identityName = identity.getName();
    log.debug("CMH-O365-Role-Assignment-Rule for identity:"+identityName);
    String emailReqd = identity.getStringAttribute("mailReqd");
    String email = identity.getEmail();
    String status = identity.getStringAttribute("status");
    if (!Util.nullSafeCaseInsensitiveEq(status, "Terminated")) {
        String oldEmpId = identity.getStringAttribute("otherEmpId");
        String isConversionProcessed = identity.getStringAttribute("isConversionProcessed");
        if (Util.isNullOrEmpty(oldEmpId) || !isConversionCase(oldEmpId) || Util.nullSafeCaseInsensitiveEq(isConversionProcessed, "YES")) {
            String startDate = identity.getStringAttribute("startDate");
            if (!status.equalsIgnoreCase("Prehire") || (Util.isNotNullOrEmpty(startDate) && startDateCheck(startDate))) {
                String userType = identity.getStringAttribute("userType");
                if (Util.nullSafeCaseInsensitiveEq(userType, "EMPLOYEE") || Util.nullSafeCaseInsensitiveEq(emailReqd, "YES")) {
                    if (Util.isNotNullOrEmpty(email)) {
                        assignRole = true;
                    }
                }
            }
        }
    }
}

if (assignRole) {
    logger.debug("CMH-Role-Assignment-Rule:O365 role assigned for the User: " + identityName);
}
logger.debug("Exiting CMH-O365-Role-Assignment-Rule:Assign O365 Role for the user based on the userType and EmailRequired");
return assignRole;
