Rule.Source:

  	import java.text.ParseException;
  	import java.text.SimpleDateFormat;
  	import sailpoint.object.Filter;
	import sailpoint.object.Identity;
	import sailpoint.object.QueryOptions;
  	import sailpoint.tools.Util;

  	SimpleDateFormat sdf = new SimpleDateFormat("MM/dd/yyyy");
  	if(newIdentity!=null){
		String oldEmpId = newIdentity.getStringAttribute("otherEmpId");
		if(Util.isNotNullOrEmpty(oldEmpId)){
	  		QueryOptions ops = new QueryOptions();
			Filter usrFilter = Filter.eq("employeeId",oldEmpId);
			ops.addFilter(usrFilter);
			Iterator existingUsers = context.search(Identity.class,ops);
			if(existingUsers!=null && existingUsers.hasNext()){
				Identity oldIden = (Identity)existingUsers.next();
				if(Util.nullSafeCaseInsensitiveEq(oldIden.getStringAttribute("status"),"TERMINATED")){
					String userStatus = newIdentity.getStringAttribute("status");
					String isConversionProcessed = newIdentity.getStringAttribute("isConversionProcessed");
					if(!Util.nullSafeCaseInsensitiveEq(isConversionProcessed,"YES")){
						if(Util.nullSafeCaseInsensitiveEq(userStatus,"ACTIVE")){
							return true;
						}else if(Util.nullSafeCaseInsensitiveEq(userStatus,"PREHIRE")){
							String startDate = newIdentity.getStringAttribute("startDate");
							try{
								if(Util.isNotNullOrEmpty(startDate)){
									Date stDate = sdf.parse(startDate);
									Date currDate = new Date();
									int daysDiff = Util.getDaysDifference(stDate,currDate);
									if(daysDiff>0 && daysDiff<=30){
										return true;
									}
								}
							}catch(ParseException ex){
								log.error("CMH-User-Conversion-Trigger-Rule: Error while parsing the date");
							}
						}
					}
				}	
			}	
		}
	}

	return false;
